try:
    import pyautogui
except:
    print("ERROR: Failed to load library 'pyautogui'.")
    print("Is the library 'pyautogui' installed?")

def click(button, x, y):
    if(button):
        if(x and y):
            pyautogui.click(button=button, x=x, y=y)
        else:
            pyautogui.click(button=button)
    else:
        print("ERROR: Button not specified.")
        print("Valid buttons: 'left', 'middle', 'right'")

def doubleClick(button, x, y):
    if(button):
        if(x and y):
            pyautogui.click(button=button, x=x, y=y)
            pyautogui.click(button=button, x=x, y=y)
        else:
            pyautogui.click(button=button)
            pyautogui.click(button=button)
    else:
        print("ERROR: Button not specified.")
        print("Valid buttons: 'left', 'middle', 'right'")

